function back( $pos ){
	global $P , $days , $esc , $S , $T , $courseName , $rand, $colorCurso, $contador;
	if( $pos == count( $P ) ){
		$str = "";
		str.= "<div class='row-fluid'><div class='box span11'>";
		str.= "<div class='box-content'>";
		str.= "<table class='table table-striped table-bordered bootstrap-datatable '>
			<thead>";
		str.= "<tr>";
		str.= "<th class='cabecera'>HORARIO</th>";
		
		$gg = 0;
		for( $i = 0 ; $i < 6 ; $i++ )
			switch ($days[ $i ]) {
				case 'LU':
					str "<th class='cabecera'>LUNES</th>";
					break;
				case 'MA':
					str "<th class='cabecera'>MARTES</th>";
					break;
				case 'MI':
					str "<th class='cabecera'>MIERCOLES</th>";
					break;
				case 'JU':
					str "<th class='cabecera'>JUEVES</th>";
					break;
				case 'VI':
					str "<th class='cabecera'>VIERNES</th>";
					break;
				case 'SA':
					str "<th class='cabecera'>SABADO</th>";
					break;
			}
			str.= "</tr>";
			str.= "</thead>";
			str.= "</tbody>";
		// 	$str.= $days[ $i ]." ";
		// $str.= " esc " .(++$esc)."<br>";


		for( $i = 8 ; $i <= 22 ; $i++ ){
			str.= "<tr>";
			str.= "<td class='hora'>".$i.":00</td>";
			for( $j = 0 ; $j < 6 ; $j++ ){
				$len = count( $S[ $j ][ $i ] );
				$cad = "";
				//echo "var len : " , $len , "\n";
				if( $len == 0 ){
					$cad = "<td class='cuerpo'></td>";
				}else if( $len == 1 ){
					$valor = $S[$j][$i][0];

					$cad = "<td class='cuerpo cuerpoDia' style='background-color:".$color."'>".$S[ $j ][ $i ][ 0 ]."</td>";
				}else{
					$gg = 1;
					str.= "<td class='cuerpo'></td>";
					// $cad = "cruce x ".$len;
				}
				 str.= $cad;
			}
			str.= "</tr>";
		}
		str.= "</tbody></table>";
		str.= "</div>";
		str.= "</div>";
		str.= "</div>";
		if( !$gg ) echo str;
		return;
	}
	//back( $pos + 1 );
	$it1 = $P[ $pos ];
	//echo $it1 , "<br>";

	foreach( $T[ $it1 ] as $sect => $V ){
		if( $sect == null ) continue;
		//var_dump( $V );
		if( $V== null ) continue;
		$doit = 0;
		foreach( $V as $vec ){
			if( $vec == null ) continue;
			$a = $vec[ 0 ];
			$b = $vec[ 1 ];
			$S[ $a ][ $b ][] = (($courseName[ $P[ $pos ] ]).$sect);
			$doit = 1;
		}
		back( $pos + 1 );
		if( $doit == 0 ) continue;
		foreach( $V as $vec ){
			if( $vec == null ) continue;
			$a = $vec[ 0 ];
			$b = $vec[ 1 ];
			array_pop( $S[ $a ][ $b ] );
		}
	}
}
	for( $j = 0 ; $j < 6 ; $j++ ) $S[ $j ] = array(array());
	for( $i = 0 ; $i <= 22 ; $i++ ){
		for( $j = 0 ; $j < 6 ; $j++ ){
			$S[ $j ][ $i ] = array();
		}
	}

	for( $i = 0 ; $i < count( $days ) ; ++$i )
		$mapaDay[ $days[ $i ] ] = $i;

	$array = explode( ' ' , $data );

	$len = count( $array );

	for( $it = 0 ; $it < $len ; $it += 6 ){
		$course = $array[ $it ];
		$section = $array[ $it + 1 ];
		$type = $array[ $it + 2 ];
		$day = $array[ $it + 3 ];
		$hBegin = (int)$array[ $it + 4 ];
		$hEnd = (int)$array[ $it + 5 ];

		$itCourse;
		//vector< map< string , vector< pair< int , int > > > > T;
		if( !array_key_exists( $course , $mapaCourse ) ){
			$itCourse = count( $mapaCourse );
			$mapaCourse[ $course ] = $itCourse;
			$courseName[] = $course;

			$sz2 = count( $T );
			$T[ $sz2 ] = array(array(array()));
		}else{
			$itCourse = $mapaCourse[ $course ];
		}
		$d = $mapaDay[ $day ];
		if( !array_key_exists( $section , $T[ $itCourse ] ) ){
			$T[ $itCourse ][ $section ] = array(array());
		}
		for( $h = $hBegin ; $h < $hEnd ; $h++ )
			$T[ $itCourse ][ $section ][] = array($d , $h);
	}
//var_dump($T);
	$cadena = $_GET['cadena'];
	$cad = strval($cadena);
	// $cad = "ST-214 ST-204 GP-304 ST-334";
	$temp = explode( ' ', $cad );
	foreach( $temp as $str )
		$P[] = $mapaCourse[ $str ];
	back( 0 );
